create table if not exists public.restaurants
(
    id              bigint           not null
        primary key,
    addr_city       varchar(50),
    cuisine         varchar(500),
    name            varchar(200)     not null,
    opening_hours   varchar(200),
    website         varchar(200),
    internet_access varchar(50),
    phone           varchar(50),
    email           varchar(100),
    smoking         varchar(100),
    lon             double precision not null,
    lat             double precision not null
);

alter table public.restaurants
    owner to postgres;

create table if not exists public.cit_users
(
    id                         bigserial
        primary key,
    is_account_non_expired     boolean not null,
    is_account_non_locked      boolean not null,
    is_credentials_non_expired boolean not null,
    is_enabled                 boolean not null,
    name                       varchar(255),
    password                   varchar(255),
    role                       varchar(255),
    surname                    varchar(255),
    username                   varchar(255)
);

alter table public.cit_users
    owner to postgres;

create table if not exists public.cit_users_favorites
(
    user_id      bigint not null
        constraint fkqohesol31ely51ws247rgbvfm
            references cit_users,
    favorites_id bigint not null
        constraint uk_thfbbfxerdncremd1nntxaqfa
            unique
        constraint fkcv1tus0stu0wm8i43rfvfl88p
            references restaurants
);

alter table public.cit_users_favorites
    owner to postgres;



